mapper.select:
SELECT * FROM (
    SELECT rownum rnum, B.* FROM (
		
		ORDER BY board_no DESC
    ) B
) WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE board

	hit = hit + 1,

 
	AND board_no = #{boardNo}

mapper.select:
SELECT count(*) FROM like

	AND id = #{id}
	AND board_no = #{boardNo}

mapper.delete:
DELETE like

	AND id = #{id}
	AND board_no = #{boardNo}

mapper.select:
SELECT menu
FROM board
WHERE menu = #{menu}
