	rent_status = '0'


	rent_status = '1'
	AND board_no = #{boardNo}
	AND rent_no = #{rentNo}

mapper.select:
SELECT count(*) FROM rent

	AND rent_status = '2'
	AND board_no = #{boardNo}
	AND rent_no = #{rentNo}

